		     if NFtpe =:= undefined -> PFtpe;
			true -> NFtpe
		     end,
		 address =
		     if PFaddress /= undefined, NFaddress /= undefined ->
			    merge_msg_ssb_address(PFaddress, NFaddress,
						  TrUserData);
